print("Filesystem FreeSpace")local function a(b)if b>=1000*1000 then return math.floor(b/(100*1000))/10 .."MB"elseif b>=1000 then return math.floor(b/100)/10 .."KB"else return b.."B"end end;local function c(d)f=fs.getFreeSpace(d)print(d.." "..a(f))end;c("/")for e,g in ipairs(peripheral.getNames())do if peripheral.getType(g)=="drive"then mount=disk.getMountPath(g)if mount then c('/'..mount)end end end