local function a(b)if fs.exists(b)then local file=io.open(b,"r")local c=file:read()file:close()return c end end;local function d(b,e)file=fs.open(b,'w')file.writeLine(e)file:close()end;local f=shell.resolve("/.passwd")if fs.exists(f)then term.write("Current Password: ")local g=read('*')if not sha256.sha256(g)==a(f)then printError("Incorrect password!")return end end;term.write("New Password: ")local h=read('*')if string.len(h)<4 then printError("Password must be 4 characters or more")return end;term.write("Repet Password: ")local i=read('*')if h==i then d(f,sha256.sha256(i))if term.isColor()then term.setTextColour(colors.lime)end;print("Password Changed")else printError("Password does not match")end