local function a()print("Usage:")print("wget <url> <filename>")end;local b={...}if#b<2 then a()return end;if not http then printError("wget requires http API")printError("Set http_enable to true in ComputerCraft.cfg")return end;local function c(d)write("Connecting to "..d.."... ")local e,f=http.checkURL(d)if not e then print("Failed.")if f then printError(f)end;return nil end;local g=http.get(d)if not g then print("Failed.")return nil end;print("Success.")local h=g.readAll()g.close()return h end;local d=b[1]local i=b[2]local j=shell.resolve(i)if fs.exists(j)then print("File already exists")return end;local k=c(d)if k then local l=fs.open(j,"w")l.write(k)l.close()print("Downloaded as "..i)end